# fizz buzz implementation
fizzbuzz: (x :Int) {
  { x 15 mod 0 = } { "fizz buzz" println }
  { x 3 mod 0 = } { "fizz" println }
  { x 5 mod 0 = } { "buzz" println }
  { true } { x println }
} cond-fun
